changeSet.sql:
CREATE TABLE public.order_products (
    order_id int8 NOT NULL,
    product_id int8 NOT NULL,
    CONSTRAINT order_products_orders_fk FOREIGN KEY (order_id) REFERENCES public.orders(id) ON UPDATE CASCADE,
    CONSTRAINT order_products_products_fk FOREIGN KEY (product_id) REFERENCES public.products(id)
);
